import './App.css';

async function fetchData(e) {
    e.preventDefault();
    const form = e.target;
    const formData = new FormData(form);

    const requestOptions = {
        method: 'POST',
        headers: {'Content-Type': 'application/json'},
        body: JSON.stringify({url: `${formData.get('url')}`}),
    };

    console.log(requestOptions.body)

    const response = await fetch('http://localhost:3001/download/mp3', requestOptions, {responseType: 'blob'}).then((res) => res.blob())
    console.log(response)

    const url = window.URL.createObjectURL(new Blob([response.data], {type: 'audio/mpeg'}));
    const link = document.createElement('a');
    link.href = url;
    link.setAttribute('download', 'video.mp3');
    document.body.appendChild(link);
    link.click();
    //const data = await response.json();
    //console.log(data.title);

}

function App() {
    return (<div>
        <h1>Youtube MP3 Downloader</h1>
        <form onSubmit={fetchData}>
            <input name="url" defaultValue="TEST"/>
            <br/>
            <button type="submit">Download</button>
        </form>
    </div>)
}

export default App;
